// src/LoginPage.js
import React, { useEffect, useRef } from 'react';
import Auth0Lock from 'auth0-lock';
import './LoginPage.css';


const domain = "dev-cktlo4j40x6gduwt.us.auth0.com";
const clientId = "XJxZL8FdzvXmBY49OPx0RWpW39q7JlCE";

const LoginPage = () => {
  const lockContainer = useRef(null);

  useEffect(() => {
    const lock = new Auth0Lock(
        clientId, // 替换为你的 Auth0 Client ID
        domain, // 替换为你的 Auth0 域名
      {
        container: lockContainer.current, // 将 Lock 嵌入到指定容器中
        auth: {
          redirectUrl: window.location.origin,
          responseType: 'token id_token',
          params: {
            scope: 'openid profile email',
          },
        },
        theme: {
          logo: 'icon.png', // 可选：左侧面板的 Logo 图片
          primaryColor: '#31324F', // 可选：主颜色
        },
        languageDictionary: {
          title: "VideoLingo" // 可选：右侧 Lock 组件的标题
        },
      }
    );

    // 显示 Lock 登录表单
    lock.show();

    // 清理 Lock 实例
    return () => lock.hide();
  }, []);

  return (
    <div className="login-container">
      <div className="left-panel">
        <div className="branding">
          <img src="icon.png" alt="Video Lingo Logo" className="logo" />
          <h1>VideoLingo</h1>
          <p>连接世界的每一帧</p>
          <footer>VideoLingo © 2023-2024</footer>
        </div>
      </div>
      <div className="right-panel">
        {/* Lock 登录组件将嵌入在此容器中 */}
        <div ref={lockContainer} className="auth0-lock-container"></div>
      </div>
    </div>
  );
};

export default LoginPage;
